import{loadSquad}from"./tabs/squad.js";import{loadActionLog}from"./tabs/actions.js";import{loadLeaderboard}from"./tabs/leaderboard.js";import{loadTribes}from"./tabs/tribes.js";import{loadSocialContent}from"./tabs/social.js";export function initializeContentLoader(){window.loadContent=async function(){const a=document.getElementById("content");a.innerHTML='<div class="text-center py-12">Loading...</div>';const t=window.state||{};try{switch(t.activeTab){case"squad":await loadSquad();break;case"actions":await loadActionLog();break;case"leaderboard":await loadLeaderboard();break;case"tribes":await loadTribes();break;case"social":await loadSocialContent();break;default:a.innerHTML=`<div class="text-center py-12 text-red-500">Unknown tab: ${t.activeTab}</div>`}}catch(t){console.error("Content load error:",t),a.innerHTML=`<div class="text-center py-12 text-red-500">${t.message}</div>`}}}