export function shortenAddress(e){return"string"!=typeof e||e.length<10?e:`${e.slice(0,6)}...${e.slice(-4)}`}export function formatDate(e,t={}){if(!e)return"";const r="string"==typeof e?new Date(e):e;return new Intl.DateTimeFormat("en-US",{year:"numeric",month:"short",day:"numeric",hour:"2-digit",minute:"2-digit",...t}).format(r)}export function formatNumber(e){return null==e?"":e.toLocaleString("en-US")}export function getTier(e){return e?e.includes("gpt-4")?"S":e.includes("gpt-3.5")?"A":e.includes("claude")?"B":"C":"U"}export function getTierColorClass(e){const t={S:"bg-purple-600",A:"bg-blue-600",B:"bg-green-600",C:"bg-yellow-600",U:"bg-gray-600"};return t[getTier(e)]||t.U}export function truncateText(e,t=100){return!e||e.length<=t?e:e.slice(0,t)+"..."}export function toCamelCase(e){return e.replace(/[-_]([a-z])/g,(e=>e[1].toUpperCase()))}export function getActionIcon(e){switch(e){case"attack":return"⚔️";case"defend":return"🛡️";case"move":return"🚶";case"remember":return"💭";case"xpost":return"🐦";case"post":return"📝";default:return"❓"}}